function Home() {
  const { addToCart, increment, decrement, getItemQuantity } = useCart();
  const offers = [
    "https://images.unsplash.com/photo-1504674900247-0877df9cc836?w=1200&q=80&auto=format&fit=crop",
    "https://images.unsplash.com/photo-1542838132-92c53300491e?w=1200&q=80&auto=format&fit=crop",
    "https://images.unsplash.com/photo-1498837167922-ddd27525d352?w=1200&q=80&auto=format&fit=crop",
  ];
  const [offerIndex, setOfferIndex] = useState(0);
  const prevOffer = () => setOfferIndex((i) => (i - 1 + offers.length) % offers.length);
  const nextOffer = () => setOfferIndex((i) => (i + 1) % offers.length);
  const { query } = useSearch();
  const filteredProducts = products.filter((p) =>
    p.name.toLowerCase().includes(query.trim().toLowerCase())
  );
  const categories = Array.from(new Set(filteredProducts.map((p) => p.category)));
  return (
    <div className="home-container">
      <h2>Available Groceries</h2>
      <div className="offers-container">
        <div className="offers-carousel">
          <button className="carousel-btn carousel-btn-left" onClick={prevOffer} aria-label="Previous">
            &#8249;
          </button>
          <img
            key={offerIndex}
            className="offers-image"
            src={offers[offerIndex]}
            alt={`Offer ${offerIndex + 1}`}
            loading="lazy"
          />
          <button className="carousel-btn carousel-btn-right" onClick={nextOffer} aria-label="Next">
            &#8250;
          </button>
        </div>
      </div>
      {categories.map((cat) => (
        <section key={cat} className="category-section">
          <h3 className="category-title">{cat}</h3>
          <div className="product-grid">
            {filteredProducts
              .filter((p) => p.category === cat)
              .map((item) => {
                const qty = getItemQuantity(item.id);
                const priceNum = parseFloat(
                  String(item.price)
                    .replace(/[^0-9.]/g, "")
                    .replace(/\.+/g, ".")
                );
                return (
                  <div key={item.id} className="product-card">
                    {qty > 0 && (
                      <span className="product-qty-badge" title="In cart">
                        {qty}
                      </span>
                    )}
                    <img
                      className="product-image"
                      src={item.image}
                      alt={item.name}
                      loading="lazy"
                    />
                    <div className="product-info">
                      <h3 className="product-name">{item.name}</h3>
                      <p className="product-price">{item.price}</p>
                    </div>
                    {qty === 0 ? (
                      <button
                        className="add-to-cart-btn"
                        onClick={() =>
                          addToCart({
                            id: item.id,
                            name: item.name,
                            priceLabel: item.price,
                            priceNumber: priceNum,
                          })
                        }
                      >
                        Add to Cart
                      </button>
                    ) : (
                      <div className="qty-controls">
                        <button className="qty-btn" onClick={() => decrement(item.id)}>-</button>
                        <span className="qty-value">{qty}</span>
                        <button className="qty-btn" onClick={() => increment(item.id)}>+</button>
                      </div>
                    )}
                  </div>
                );
              })}
          </div>
        </section>
      ))}
    </div>
  );
}

export default Home;
